import './App.css';
import React, { useState } from 'react';
import headerImg from "./images/bg-header-mobile.svg"
import Card from './Card';
import { JobData } from "./data"
import { FaTimes } from "react-icons/fa"

function App() {

  const [data, setData] = useState(JobData)
  const [inputValue, setInputValue] = useState("")
  const [filterData, setFilterData] = useState(["chsd", "dxuhn", "jidhnu", "jidhcnu"]);


  const JobCard = data.map((items) => {

    return (<Card key={items.id} Data={items} />)
  }

  )

  const deleteFilter = (item) => {
    setFilterData(filterData.filter((itemA) => itemA !== item))
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={headerImg} alt="pic" />
      </header>
      <div className='Job filter'>
        <form className='items' ons>
          {filterData.map((item, index) => {
            return (
              <div
                key={index}
                className='filterItems'>
                <span
                  className="filterItem"
                >{item}</span>
                <FaTimes className='fa times'
                  onClick={() => deleteFilter(item)}
                />
              </div>
            )
          })}
          <input
            type="text"
            value={inputValue}
            onChange={(e) =>
              setInputValue(e.target.value)
            }
          />
        </form>
        <p className='clear'
          onClick={() => setFilterData([])}
        >Clear</p>
      </div>

      <div className='Jobs'>
        {JobCard}
      </div>


    </div>
  );
}

export default App;
